mapper.select:
SELECT r_num, r_name, r_add, r_food, r_tel, r_menu, r_fileName, r_detail, r_open, r_close, r_category
FROM rest
WHERE r_num = #{r_num}

SELECT r_like_num
FROM r_like
WHERE mem_num = #{mem_num}
AND r_num = #{r_num}

mapper.insert:
  	
		INSERT 
			INTO r_like
				(
					r_num,
					mem_num
				)		
		VALUES(
			#{r_num},
			#{mem_num}
			)
